export const buy = createAsyncThunk<boolean, undefined, AsyncThunkConfig>(
  'item/buy',
  async (_, thunkAPI) => {
    const { getState } = thunkAPI;
    const { carts } = getState().items;
    const { money, coin } = getState().user.user;
    const totalMoney = carts.reduce((acc, cur) => {
      if (cur.paymentMethod === 'money')
        return acc + cur.item.price.money! * cur.quantity;
      return acc;
    }, 0);
    const totalCoin = carts.reduce((acc, cur) => {
      if (cur.paymentMethod === 'coin')
        return acc + cur.item.price.coin! * cur.quantity;
      return acc;
    }, 0);
    if (money < totalMoney) {
      toast.error('Không đủ tiền');
      return false;
    }
    if (coin < totalCoin) {
      toast.error('Không đủ coin');
      return false;
    }
    const response = await fetchNui<{
      status: 'success' | 'error';
      message: string;
    }>('buyItem', carts);
    if (response.status === 'error') {
      toast.error(response.message);
      return false;
    }
    toast.success(response.message);
    return true;
  }
);

const itemSlice = createSlice({
  name: 'item',
  initialState,
  reducers: {
    setItems(state, action: PayloadAction<Item[]>) {
      state.items = action.payload;
      state.selectedOptions = [];
    },
    clearItems(state) {
      state.items = initialState.items;
      state.selectedOptions = [];
    },
    clearSelectedOptions(state) {
      state.selectedOptions = initialState.selectedOptions;
    },
    setSelectedOptions(state, action: PayloadAction<string[]>) {
      state.selectedOptions = action.payload;
    },
    addSelectedOptions(state, action: PayloadAction<string>) {
      const options = new Set(state.selectedOptions);
      options.add(action.payload);
      state.selectedOptions = Array.from(options);
    },
    removeSelectedOptions(state, action: PayloadAction<string>) {
      const options = new Set(state.selectedOptions);
      options.delete(action.payload);
      state.selectedOptions = Array.from(options);
    },
    setSelectedItem(state, action: PayloadAction<Item>) {
      state.selectedItem = action.payload;
    },
    clearSelectedItem(state) {
      state.selectedItem = undefined;
    },
    addCartItem(state, action: PayloadAction<Omit<CartItem, 'id'>>) {
      console.log(action.payload);
      if (action.payload.quantity === null || action.payload.quantity <= 0) {
        toast.error('Số lượng phải lớn hơn 0');
        return;
      }
      const id = faker.string.uuid();
      state.carts.push({ ...action.payload, id });
    },
    removeCartItem(state, action: PayloadAction<string>) {
      const index = state.carts.findIndex((cart) => cart.id === action.payload);
      if (index === -1) return;
      state.carts.splice(index, 1);
    },
    increaseCartItem(state, action: PayloadAction<string>) {
      const index = state.carts.findIndex((cart) => cart.id === action.payload);
      if (index === -1) return;
      state.carts[index].quantity++;
    },
    decreaseCartItem(state, action: PayloadAction<string>) {
      const index = state.carts.findIndex((cart) => cart.id === action.payload);
      if (index === -1) return;
      state.carts[index].quantity--;
      if (state.carts[index].quantity === 0) {
        state.carts.splice(index, 1);
      }
    },
    setShowConfirmModal(state, action: PayloadAction<boolean>) {
      if (action.payload === true) {
        if (state.carts.length === 0) {
          toast.error('Giỏ hàng trống');
          return;
        }
      }
      state.showConfirmModal = action.payload;
    },
    setShowDonateModal(state, action: PayloadAction<boolean>) {
      state.showDonateModal = action.payload;
    },
    setDonateData(state, action: PayloadAction<BankAccount[]>) {
      state.donateData = action.payload;
    },
    setCategories(state, action: PayloadAction<Category[]>) {
      state.categories = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(buy.fulfilled, (state, action) => {
      if (action.payload === true) {
        state.carts = [];
        state.showConfirmModal = false;
        toast.success('Mua thành công');
      }
    });
  },
});

